    private void comprobarUsuario() {
        
        AccesoTerminal terminal = new AccesoTerminal();
        AccesoUsuario acceso = new AccesoUsuario();
        
        if (txtContraseniaUno.getText().isEmpty() || txtContraseniaDos.getText().isEmpty()) {
            Mensaje.manipulacionExcepciones("critico", "Por favor ingrese la nueva clave en ambos cuadros de texto.", "Cambio de Clave");
        } else {
            if (txtContraseniaUno.getText().equals(txtContraseniaDos.getText())) {
                int id_usuario = acceso.retornaIdUsuario(Inventory.lblUsuario.getText(), Inventory.lblUsuario.getText(), Inventory.lblTerminal.getText());
                String resultado = acceso.actualizarClaveUsuario(txtContraseniaUno.getText(), id_usuario, Inventory.lblUsuario.getText(), Inventory.lblTerminal.getText());
                Mensaje.manipulacionExcepciones("informacion", resultado, "Cambio de Clave");
            } else {
                txtContraseniaUno.setText("");
                txtContraseniaDos.setText("");
                Mensaje.manipulacionExcepciones("crietico", "Las contraseñas no coinciden", "Cambio de Clave");
            }
        }
        
    }

    private void btnAceptarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAceptarActionPerformed
        comprobarUsuario();
    }//GEN-LAST:event_btnAceptarActionPerformed

